INSERT INTO `development_works` (`works_id`, `works_title`, `works_place`, `works_description`, `works_date`) VALUES
(2, 'মাদ্রাসা এমপিওভুক্ত ঃ আড়াইশ’ মসজিদ মাদ্রাসার উন্নয়ন ঃ ৩টির একাডেমিক ভবন নির্মাণ', 'মসজিদ-মাদ্রাসায় ফজলে হোসেন বাদশার উন্নয়ন কর্মকাণ্ড', 'তবিবুর রহমান মাসুমঃ রাজশাহী সদর আসনের সংসদ সদস্য জননেতা ফজলে হোসেন বাদশা বিগত ৫ বছরে মসজিদ ও মাদ্রাসার উন্নয়নে ব্যাপক কর্মসূচী বাস্তবায়ন করেছেন। নগরীর এমন কোন মসজিদ ও মাদ্রাসা নেই যেখানে সাংসদ বাদশা নামক একজন উন্নয়ন শিল্পীর তুলির আঁচড় পড়েনি। সংসদ সদস্য জননেতা ফজলে হোসেন বাদশা’র বিগত ৫ বছরে এসব উন্নয়ন কর্মসূচীর মধ্যে রয়েছে নগরীর হড়গ্রাম বাজারে সম্পূর্ণ নতুনভাবে একটি মসজিদ নির্মাণ, কোর্ট দারুস সালাম আলিয়া মাদ্রাসার মহিলা বিভাগের এসমপিওভুক্তি, ২৩১টি মসজদের উন্নয়নে বরাদ্দ, ৩টি মাদ্রাসার একাডেমিক ভবন নির্মান এবং ৩২ মাদ্রাসার উন্নয়নে বরাদ্দ। এ প্রসঙ্গে কোর্ট দারুস সালাম আলিয়া মাদ্রাসার অধ্যক্ষ(ভারপ্রাপ্ত) মোঃ লিয়াকত আলী বলেন, তাঁর মাদ্রাসায় ৭০ লাখ টাকা ব্যায়ে একাডেমিক ভবন নির্মাণ, মাদ্রাসা সংস্কার ও আসবাবপত্র প্রদান, মাদ্রার মহিলা বিভাগের এমপিওভুক্তি করেছেন সাংসদ বাদশা। তিনি বলেন শুধু এই মাদ্রাসায় নয়, আমার জানা মতে নগরীর প্রায় সব মসজিদ, মাদ্রাসা, অন্যান্য ধর্মীয় প্রতিষ্ঠান ও শিক্ষা প্রতিষ্ঠানের উন্নয়নে ভূমিকা রেখেছেন। আমি তাঁকে অন্তরের অন্তঃস্থল থেকে শ্রদ্ধা, দোয়া ও কৃতজ্ঞতা জানাচ্ছি। জামিয়া ইসলামিয়া শাহ মখদুম(রহঃ) মাদ্রাসার অধ্যক্ষ ও কেন্দ্রীয় ঈদগাহ’র ইমাম মুফতি মোঃ শাহাদত আলী বলেন, একজন জনপ্রতিনিধির কাছে সাধারণ মানুষের যা প্রত্যাশা থাকে, তার প্রায় সবটাই পূরণ করার চেষ্টা করেছেন সদর আসনের সংসদ সদস্য ফজলে হোসেন বাদশা। আমার জানা মতে নগরীর এমন কোন মসজিদ ও মাদ্রাসা নেই যেখানে সাংসদ বাদশা উন্নয়ন করেননি। এজন্য আমি তাঁর প্রতি কৃতজ্ঞতা জ্ঞাপন করছি। রাজশাহী কলেজ কেন্দ্রীয় মসজিদের খতিব মোঃ মাকছুল উল্লাহ বলেন, নগরীর মসজিদ, মাদ্রাসা, অন্যান্য ধর্মীয় প্রতিষ্ঠান ও শিক্ষা প্রতিষ্ঠানের উন্নয়নে সদর আসনের সংসদ সদস্য ফজলে হোসেন বাদশা ভূমিকা রেখেছেন। প্রচুর কাজ হয়েছে বিগত ৫ বছরে। এজন্য তিনি অবশ্যই ধন্যবাদের পাত্র। আমি তাঁর সুস্বাস্থ্য ও দীর্ঘায়ু কামনা করছি। রাজশাহী সদর আসনের সংসদ সদস্য জননেতা ফজলে হোসেন বাদশা বিগত ৫ বছরে ২৩১টি মসজিদের উন্নয়নে বরাদ্দ দিয়েছেন। এগুলো হলোঃ বুলনপুর জিয়ানগরস্থ মসজিদ-ই-জান্নাতুল ফেরদাউস এ উন্নয়ন, মোল্লাপাড়া হড়গ্রামকলোনীস্থ জান্নাতুল ফেরদৌস মসজিদের উন্নয়ন, হড়গ্রাম বাজার জামে মসজিদের উন্নয়ন, হড়গ্রাম পূর্বপাড়া জামে মসজিদের উন্নয়ন, দারুস সালাম জামে মসজিদের উন্নয়ন মহিষবাথান, দারউস সালাম জামে মসজিদের উন্নয়ন অক্টরমোড় কাজলা, হড়গ্রাম কলোনী জামে মসজিদের উন্নয়ন, দারুস সালাম কামিল মাদ্রাসা মহিলা শাখার উন্নয়ন বাইতুল মামুর জামে মসজিদের উন্নয়ন কাশিয়াডাংগা, গোলজারবাগ জামে মসজিদের উন্নয়ন, লক্ষীপুর ভাটাপাড়া জামে মসজিদের উন্নয়ন, লক্ষিপুর ভাটাপাড়া, ফারুকিয়া মাদ্রাসার উন্নয়ন, জান্নাতুল মাযাম জামে মসজিদের উন্নয়ন, নবাবগঞ্জ ঘোষপাড়া, বায়তুল সালাহ্ জামে মসজিদের উন্নয়ন রানীনগর সাধুর মোড়, মাসজিদুল ফাতাহ লক্ষিপুর হার্ট ফাউন্ডেশনের উন্নয়ন, সায়েরগাছা জামে মসজিদের উন্নয়ন, মাল্লাপাড়া বায়তুল মামুর জামে মসজিদের উন্নয়ন, হলদারপাড়া জামে মসজিদের উন্নয়ন রাজপাড়া, মহিষবাথান কেন্দ্রীয় জামে \r\nমসজিদের উন্নয়ন, আহলুস্ ফুফাফাহ্ জামে মসজিদের উন্নয়ন, হড়গ্রাম পীরসাহেব পাড়া, লক্ষিপুর ভাটাপাড়া শাহী জামে মসজিদের উন্নয়ন, মুলিডাংগা জামে মসজিদের উন্নয়ন, বায়তুলফালা বড় জামে মসজিদের উন্নয়ন, রানীবাজার জামে মসজিদের উন্নয়ন, ওয়াক্তিয়া জামে মসজিদের উন্নয়ন ছোটবনগ্রাম উত্তরপাড়া, বায়তুননূর জামে মসজিদের উন্নয়ন ১নং(বাঁধের ধার), ধরমপুর পূর্বপাড়া জামে মসজিদের উন্নয়ন, মসজিদ আবু বক্কর সিদ্দিক জামে মসজিদের উন্নয়ন, মুন্সিপাড়া জামে মসজিদের উন্নয়ন, মুলিপাড়া, রেশম পট্টি জামে মসজিদের উন্নয়ন, বালিয়াপুকুর জামে মসজিদের উন্নয়ন, খোজাপুর আল আকসা জামে মসজিদের উন্নয়ন, কয়েরদাড়া বিলপাড়া জামে মসজিদের উন্নয়ন, কাজলা আহলে হাদিস জামে মসজিদের উন্নয়ন, টিকাপাড়া আহম্মদপুর, জামে মসজিদের উন্নয়ন, জান্নতুল ফেরদৌস জামে মসজিদের উন্নয়ন, কখতিয়ারাবাদ জামে মসজিদের উন্নয়ন সপুরা, সাগরপাড়া বায়তুল আমান মসজিদের উন্নয়ন, সাগরপাড়া রিয়াজুল জান্নাত জামে মসজিদের উন্নয়ন, সাগরপাড়া জামে মসজিদের উন্নয়ন, সাগরপাড়া পুরাতন জামে মসজিদের উন্নয়ন(বোসপাড়া), আলুপট্টি জামে মসজিদের উন্নয়ন, পাঁচানিরমাঠ ঈদগাহ জামে মসজিদে উন্নয়ন, মহলদারপাড়া জামে মসজিদের উন্নয়ন, আহম্মদপুর বড় জামে মসজিদের উন্নয়ন, নূরে আয়য়ুব আলী জামে মসজিদের উন্নয়ন, বাইতুল হামদ জামে মসজিদ (মেহেরচন্ডী বুধপাড়া), কাজিহাটা ১ নং জামে মসজিরেদ উন্নয়ন, কাজিরহাটা মাদরাসা উন্নয়ন, দরগাহ মাদ্রাসার উন্নয়ন, হোসেনীগঞ্জ শেখপাড়া জামে মসজিদের উন্নয়ন, পাঠানপাড়া মাদ্রাসা উন্ন