INSERT INTO `self_biography` (`id`, `name`, `author`, `pages_title`, `pages_description`, `pages_image`) VALUES
(1, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'এক. ভূমিকা ', 'গণমানুষের প্রাণপ্রিয় নেতা ফজলে হোসেন বাদশা স্বীয় কর্মগুনেই রাজশাহীসহ সারাদেশে সুপরিচিত। তাঁর রাজনৈতিক প্রজ্ঞা ও জীবন দর্শন আজকের এবং আগামী প্রজন্মের সামনে উজ্জ্বল দৃষ্টান্ত হিসেবে বিবেচিত, একথা বেশ জোরের সাথেই বলা যায়। আপসহীন, সৎ, নীতিবাদি ও আদর্শমানে চরিত্রের অধিকারী জননেতা ফজলে হোসেন বাদশা সর্বস্তরের মানুষের বড়ই আপনজন। তাঁর নেতা সুলভ আচার-আচরণ অবশ্যই প্রশংসার দাবী রাখে। সবচেয়ে বড় কথা তাঁর সততা, মানবপ্রেম এবং দেশপ্রেম তাঁকে তৃনমূল সাধারণ পর্যায়ের মানুষের কাছে অবাধে মেলামেশার সুযোগ করে দিয়েছে। আর যে কারণে তাঁর গ্রহণযোগ্যতাও আকাশচুম্বী। নিজের দল বা ১৪ দলের নেতা হিসেবে শুধু নয়, ব্যক্তিগত কর্মপ্রয়াসে বা গুনাবলী তাঁকে বিভিন্ন দল ও গোত্রের মানুষের কাছে একবুক কাছের মানুষ ও প্রিয়জন হিসেবে চিহ্নিত করেছে। তিনি এমন ধরনের রাজনীতিক যে, সর্বদাই তাঁর দরোজা সবার জন্যই উন্মক্ত। তিনি সবার কথাই মনোযোগ সহকারে শোনেন এবং আন্তরিকভাবে সমস্যা সমাধানের চেষ্টা করেন।  \r\n\r\nসত্যি কথা বলতে কী আজকের পেশী শক্তি, অবৈধ অস্ত্র এবং কালো টাকার কাছে রাজনীতি ডিফিকাল্ট হয়ে গেছে। সেখানে তাঁর মতো সহজ, সরল, সৎ ও নিবেদিত প্রাণ রাজনীতিক বিরল দৃষ্টান্তের মতো। ছাত্র জীবনেই তাঁর রাজনীতিতে হাতে খড়ি এবং ২০০৮ সালে জাতীয় সংসদ সদস্য হিসেবে রাজশাহী সদর-২ আসনে নির্বাচিত হওয়ার পূর্বে এবং পরে তিনি সর্বদাই রাজশাহীর আর্থ-সামাজিক উন্নয়নের লক্ষ্যে দল-মত নির্বেশেষে সর্বস্তরের মানুষকে সঙ্গে নিয়ে আন্দোলন ও সংগ্রামে নেতৃত্ব দান করেছেন এবং দাবী আদায়ের মধ্য দিয়ে নিজের অবস্থানকে পাকাপোক্ত ও সুদৃঢ় ভিক্তি উপর দাঁড় করাতে সক্ষম হয়েছেন। এজন্য তাঁকে বহুবার জেল জীবনসহ নানা অত্যাচার ও নির্যাতন সহ্য করতে হয়েছে। মোটকথা, তাঁর সাহসী, বলিষ্ঠ ও আপসহীন নেতৃত্ব দানের কারণেই কাক্সিক্ষত লক্ষ্য অর্জনে তিনি সমর্থ হয়েছেন। কোন রকমের প্রলোভনের কাছে বা ব্যক্তিস্বার্থ উদ্ধারের লক্ষে তিনি কখনই তাঁর মেধা, মনন ও মস্তিস্ক বিক্রি করেননি। আর এজন্যই তিনি তাঁর স্পষ্টবাদিতা, দায়িত্ব ও কর্তব্য সঠিকভাবে পালনে সর্বদাই সোচ্চার হয়েছেন।', 'first.jpg'),
(2, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'দুই. জীবন কথা', 'ফজলে হোসেন বাদশা ১৯৫২ সালের ১৫ আক্টোবর রাজশাহী মহানগরীর হড়গ্রাম বাজারে নিজ বাড়িতে জন্মগ্রহণ করেন। তাঁর পিতা এ্যাডভোকেট খন্দকার আশরাফ হোসেন এবং মাতা দিলারা হোসেন। তাঁর পিতা রাজশাহী জজকোর্টের একজন বিশিষ্ট আইনজীবী ছিলেন। তিনি রাজশাহীতে প্রগতিশীল রাজনীতির সাথে জড়িত ছিলেন এবং ভাষা আন্দোলন হতে স্বাধীনতা সংগ্রাম পর্যন্ত গুরুত্বপূর্ণ ভুমিকা পালন করেছেন। তিনি রাজনীতি ছাড়াও আর্থ-সামাজিক বিভিন্ন কর্মকা-ের সঙ্গে নিবিড়ভাবে জড়িত ছিলেন। তিনি রাজশাহী বারে আইনজীবী সমিতির সভাপতি হিসেবে দায়িত¦ পালন করেছেন এবং রাজশাহী আইন কলেজ প্রতিষ্ঠায় অবদান রেখেছেন। ফজলে হোসেন বাদশার মা দিলারা হোসেন মূলত একজন আদর্শ গৃহিনী, সেসঙ্গে স্বামী এবং সার্থক রাজনৈতিক জীবন গড়ে তোলার ক্ষেত্রে সর্বদাই সহযোগিতার হাত সম্প্রসারিত করেছেন। তিনি স্বনির্ভর হওয়ার জন্য গরীব ও দু:স্থ মহিলাদের উর্পাজনের লক্ষে রাজশাহী মহিলা শিল্প প্রতিষ্ঠান পরিচালনায় আন্তরিকভাবে দায়িত্ব পালন করেন। এই প্রতিষ্ঠানের সাধারণ সম্পাদক বিশিষ্ট সমাজসেবী ও ভাষাসৈনিক বেগম মনোয়ারা রহমানকে তিনি সর্বদাই সহযোগিতা করেছেন। সমাজের পিছিয়ে পড়া নারীদের স্বাবলম্বী হিসেবে গড়েতোলার পেছনে তাঁর অবদান স্মরণযোগ্য। জনাব বাদশার জীবন সঙ্গিনী তসলিমা খাতুন একজন কলেজ শিক্ষক এবং নারী অধিকার আদায়ে বাংলাদেশ নারী মুক্তি সংসদ কেন্দ্রীয় কমিটির অন্যতম নেত্রী। তাঁদের একমাত্র কন্যা ফাহিজা নুযহাত জয়ী ব্রাক বিশ্ববিদ্যালয়ে অধ্যয়নরত। আদর্শ পিতা ও মায়ের কৃতী সন্তান ফজলে হোসেন বাদশা লেখাপড়ার পাশাপাশি খেলাধুলায় নিয়মিত অংশগ্রহণ করেন। তিনি রাজশাহী মহানগরীর পশ্চিমাঞ্চলে ছাত্র ও যুবকদের সুসংগঠিত করার মাধ্যমে খেলাধুলার জন্য দিগন্ত প্রসারী সংঘ প্রতিষ্ঠায় অগ্রণী ভূমিকা পালন করেন এবং এই সংঘের স্থায়ী পরিষদের চেয়ারম্যান হিসাবে দায়িত্বরত আছেন। এছাড়াও তিনি রাজশাহীর বিভিন্ন ক্রীড়া ও সামাজিক সাংস্কৃতিক-সংগঠনের সঙ্গে সক্রিয়ভাবে জড়িত রয়েছেন।', 'second.jpg'),
(3, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'তিন. শিক্ষা জীবন', 'বাড়িতেই মায়ের কাছে এবং বড় বোনের কাছে সর্বপ্রথম লেখাপড়ায় হাতে খড়ি। তারপর তিনি ১৯৬৭ সালে রাজশাহী কলেজিয়েট স্কুল থেকে এসএসসি, ১৯৭০ সালে নিউ গভ. ডিগ্রি কলেজ থেকে এইচএসসি, রাজশাহী বিশ্ববিদ্যালয় হতে অর্থনীতি বিষয়ে অনার্স ডিগ্রিসহ এমএ  এবং এলএলবি ডিগ্রি অর্জন করেন। তিনি রাজশাহী জজকোর্টের একজন এ্যাডভোকেট হিসেবেও নিবন্ধিত রয়েছেন।', 'third.jpg'),
(4, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'চার. রাজনৈতিক জীবন ', 'ফজলে হোসেন বাদশা পরিবারিক রক্তের ধারায় প্রগতিশীল ও গণতান্ত্রিক আন্দোলন ও সংগ্রামের মধ্যে দিয়ে রাজনৈতিক জীবন শুরু করেন। ঊনসত্তরের ছাত্র গণ আন্দোলনের প্রাক্কালে তিনি পূর্ব পাকিস্থান ছাত্র ইউনিয়ন মেনন গ্র“পের প্রথমে সদস্য এবং পরে পশ্চিমাঞ্চলের আঞ্চলিক কমিটির  সভাপতি ছিলেন। প্রগতিশীল চিন্তা চেতনার ধারক ও বাহক পিতার আদর্শমতি জীবন তাঁর রাজনৈতিক জীবনের বিশেষ প্রভাব পরিলক্ষিত হয়। সেসঙ্গে হাজার বছরের শ্রেষ্ঠ বাঙ্গালি জাতির জনক বঙ্গবন্ধু শেখ মুজিবুর রহমান, মজলুম জননেতা মাওলানা আবদুল ভাসানী, কমরেড মনি সিং, কমরেড অমল সেন, রাশেদ খান মেনন এবং রাজশাহীর রাজনীতিতে বটবৃক্ষ জাতীয় নেতা শহীদ এএইচএম কামারুজ্জামান, জননেতা এম আতাউর রহমান, এ্যাডভোকেট  মহসীন প্রামাণিক, কমরেড মহসীন আলী, অধ্যাপক রুহুল আমিন প্রামাণিক প্রমুখ জাতীয় ও আঞ্চলিক রাজনৈতিক নেতৃবৃন্দ তাঁর রাজনৈতিক জীবনের প্রস্তুতি পর্বে অনুপ্রেরণার মূল উৎস হিসেবে তাঁকে অনুপ্রাণিত করেছেন। ঊনসত্তরের গণ-আন্দোলনে তৎকালীন পূর্ব পাকিস্তানের রাজশাহীতে ব্যাপক গণজাগরণ সৃষ্টি হয়। ১৯৬৯ সালের ১৮ ফেবব্রুয়ারি রাজশাহী বিম্ববিদ্যালয়ের রসায়ন বিভাগের শিক্ষক ও প্রক্টর ড. শামসুজ্জোহা এবং রাজশাহী সিটি কলেজের ছাত্র নূরুল ইসলাম শহীদ হন। ছাত্র ও শিক্ষকের সম্মিলিত রক্তের ধারায় পাকিস্তানের প্রেসিডেন্ট সামরিক জান্তা আয়ুব খানের পতন ঘটে এবং আরেক স্বৈরশাসক ইয়াহিয়া খান দায়িত্ব গ্রহণ করেন। জাতির সেই সংকটময় মুর্হূতে তরুণ ছাত্রনেতা ফজলে হোসেন বাদশা পাকিস্তানি স্বৈরশাসকদের বিরুদ্ধে সর্বাত্মক সংগ্রামে সক্রিয় ভূমিকা পালন করেন। বিশেষত সেসময় ছাত্র সংগ্রাম পরিষদ গড়ে তুলতে তিনি উদ্যোগী ছিলেন।\r\n\r\nউল্লেখ্য ১৯৭০ সালে পাকিস্তানে সাধারণ নির্বাচন অনুষ্ঠিত হয় এবং বঙ্গবন্ধুর নেতৃত্বে  আওয়ামী লীগ সংখ্যাগরিষ্ঠ আসন লাভ করে। তিনি সমগ্র পাকিস্তানে সরকার গঠনের ক্ষমতা লাভ করেন। কিন্তু ছলে-বলে-কৌশলে পশ্চিম পাকিস্তানি সামরিক জান্তা ও শাসকশ্রেণি বঙ্গবন্ধুকে কোন ক্রমেই সরকার গঠনের সুযোগ না দিয়ে রাতের অন্ধকারে বাঙ্গালিদের হত্যাযজ্ঞ শুরু করে। এজন্য বঙ্গবন্ধু বাংলাদেশের স্বাধীনতার ঘোষণা দেন এবং মুক্তিযুদ্ধে অংশগ্রহণের জন্য ছাত্র শিক্ষকসহ সর্বস্তরের মানুষকে নির্দেশ দেন। সে সময় ফজলে হোসেন বাদশা মুক্তিযুদ্ধের প্রস্তুতিপর্বে তাঁর সহযোদ্ধাদের নিয়ে ব্যাপকভাবে তৎপরতা শুরু করেন। পাকিস্তানি সামরিক জান্তাদের দৃষ্টির আড়ালে গোপন প্রচার এবং মুক্তিযুদ্ধে সামরিক প্রস্তুতি গ্রহণেও তিনি জীবন বাজি রেখে গুরুপ্তপূর্ণ ভূমিকা পালন করেন। রাজশাহী পুলিশ লাইনে পুলিশের প্রতিরোধ যুদ্ধসহ শহরের পশ্চিমাঞ্চলে যুদ্ধের শুরুতেই তিনি ছাত্র ও যুবকদের সংগঠিত করেন। মুক্তিযুদ্ধে তিনি সক্রিয়ভাবে অংশগ্রহণ করেন এবং তিনি প্রথমে ভারতের ত্রিপুরা রাজ্যের আগরতলার মেলাঘর মুক্তিযোদ্ধা ক্যাম্পে যোগদান করেন। পরবর্তী পর্যয়ে তিনি মুর্শিদাবাদ জেলার পানিপিয়া মুক্তিযোদ্ধা ক্যাম্পে যোগ দেন। তিনি মুক্তিযোদ্ধাদের সংগঠিত করতে কিছু সময় ধুলাউড়ি মুক্তিযোদ্ধা ক্যাম্পেও অবস্থান করেন। মুক্তিযুদ্ধের মাধ্যমে ত্রিশ লক্ষ শহীদের এক সাগর রক্তের বিনিময়ে বাংলাদেশ প্রতিষ্ঠার পর তিনি প্রগতিশীল রাজনীতির সঙ্গে সম্পৃক্ত থেকে রাজশাহীতে বিভিন্ন সামাজিক ও রাজনৈতিক আন্দোলনে নেতৃত্ব দান করেন। ১৯৭৫ সালে জাতির জনক বঙ্গবন্ধু শেখ মুজিবুর রহমান ও জাতীয় চার নেতাকে হত্যার পর সামরিক শাসক  জিয়াউর রহমান ক্ষমতা দখল করেন। একপর্যায়ে জাতির জনকের হত্যার বিচার করা যাবেনা এ মর্মে নির্দেশ জারি করা হয় এবং স্বাধীনতা বিরোধীদের  তিনি ক্ষমতার গুরুত্বপূর্ণ পদে আসীন করেন। সেসময় ছাত্রনেতা ফজলে হোসেন বাদশা সামারিক শাসনের পরিবর্তে সাংবিধানিক শাসন পুন:প্রতিষ্ঠাসহ বঙ্গবন্ধু, জাতীয় নেতৃবৃন্দ এবং সকল রাজনৈতিক হত্যাকা-ের দৃষ্টান্তমূলক বিচারের জন্য তিনি জনমত গঠনে নিজেকে নিয়োজিত করেন এবং রাজশাহীসহ সারাদেশে প্রগতিশীল ছাত্র আন্দোলন সংগঠিত করার লক্ষে তিনি পুরোধা ব্যক্তিত্ব হিসেবে সাহসী ভুমিকা পালন করেন। এ সময় তিনি রাজশাহী বিশ্ববিদ্যালয়ের ছাত্র সমাজের অধিকার আদায়ের  লক্ষ্য পাঁচ দফা দাবি উথাপন করেন । সে সঙ্গে বিশ্ববিদ্যালয় থেকে স্বাধীনতা বিরোধীদের বিতাড়নের আন্দোলনের নেতৃত্ব দান করেন । এজন্য তাকে বার বার গ্রেফতার করে রাজশাহী কেন্দ্রীয় কারাগার এবং ঢাকা কেন্দ্রীয় কারাগারে রাখা হয় । তিনি যখন কেন্দ্রীয় কারাগারে বন্দী জীবন যাপন করছিলেন, সেসময় কারাগারে বন্দীদের উপর নির্যাতন ও নিপীড়নের বিরুদ্ধেও আন্দোলন গড়ে তোলেন । এই কারা আন্দোলন দমনে কারাগারে গুলি চলে। তিনজন বন্দী মৃত্যুবরণ করেন। এর ফলে আন্দোলন আরো তীব্র আকার ধারণ করে । এ জন্য তৎকালীন স্বরাষ্ট্রমন্ত্রী রাজশাহী কেন্দ্র্রীয় কারাগারে আসেন। ফজলে হোসেন বাদশাসহ কারাবন্দী নেতাদের সাথে আলোচনা করে। তিনি বন্দীদের অনেক দাবি মেনে নেন। তখন কারাগারের ভিতরে তাঁর নেতৃত্বে মৃত তিন বন্দীর স্মরণে একটি শহীদ মিনার নির্মাণ করা হয় । যা এখনও বিদ্যমান রয়েছে ।\r\n\r\n \r\nউল্লেখ্য ১৯৭৮ সাল থেকে রাজশাহী বিশ্ববিদ্যালয়ে তাঁর নেতৃত্বে ৫ দফা দাবি আদায়ের লক্ষ্যে ছাত্র আন্দোলন শুরু হয়। ১৯৭৯ সালের মার্চ মাসে তিনি ছাত্রদের গণ অনশনের ডাক দেন। সেই আন্দোলনে সঠিক ও সাহসী নেতৃত্ব দানের কারণে সাধারণ ছাত্রদের  কাছে তাঁর গ্রহনযোগ্যতা বহুলাংশে বৃদ্ধি পায়। সে সময় জেনারেল জিয়াউর রহমানের দুইমন্ত্রী অনশনরত ছাত্রদের দেখতে রাজশাহী বিশ্ববিদ্যালয়ে আসেন। এজন্য ছাত্র সমাজ বিক্ষোভে ফেটে পড়েন। ছাত্ররা সেই মন্ত্রীদ্বয়কে আটক করেন। একটা সমঝোতার পর ছাত্ররা দুই মন্ত্রীকে ছেড়ে দেয়। কিন্তু তারপরেও পুলিশ ও বিডিআর সাধারণ ছাত্রদের উপর গুলি চালায়। পরবর্তীতে সরকার ফজলে হোসেন বাদশাসহ বহু ছাত্রনেতাকে গ্রেফতার করে জেলে পাঠায়। তিনি ১৯৮০ সালে রাকসুর ভিপি নির্বাচিত হন। ১৯৮০ সালের ৬ ডিসেম্বর তিনি বাংলাদেশ ছাত্রমৈত্রী প্রতিষ্ঠা করেন এবং এই ছাত্র সংগঠনের সভাপতি নির্বাচিত হন। পরবর্তী পর্যায়ে তিনি যুবমৈত্রী গঠন করেন এবং সভাপতির দায়িত্ব পালন করেন।\r\n\r\n', 'fourth.jpg'),
(5, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'চার. রাজনৈতিক জীবন ', 'তিনি ১৯৮১ সালে রাজশাহী জেলার অবহেলিত ও নিপীড়িত রিকসা চালকদের সংগঠিত করেন। এছাড়াও কৃষি ও মিল শ্রমিকদের আন্দোলনে অগ্রণী ভূমিকা পালন করেন। ১৯৮২ সালে তিনি সামরিক স্বৈরাচার বিরোধী সংগ্রাম গড়ে তুলতে ঢাকায় কেন্দ্রীয় ছাত্র সংগ্রাম পরিষদ গঠন করেন এবং ঢাকাসহ সারাদেশে তিনি ছাত্রদের সামরিক সরকারের বিরুদ্ধে বলিষ্ঠ ও সংগ্রামী ভূমিকা পালনের জন্য উদাত্ত আহক্ষান জানান। এই সামরিক শাসন জারীর বিরুদ্ধে তিনি রাজশাহী বিশ্ববিদ্যালয়ের ছাত্রদের নিয়ে সামরিক জান্তার বিরুদ্ধে প্রথম প্রতিবাদ এবং কালো পতাকা প্রর্দশন করেন। ১৯৮৩ সালের ১৪ ফেব্রুয়ারি অর্থাৎ মধ্য ফেব্রুয়ারির আন্দোলনে কেন্দ্রীয় পর্যায়ে ছাত্রনেতা হিসেবে তিনি প্রথম সারিতে ছিলেন। ঢাকায় সচিবালয় ঘেরাও আন্দোলনে নেতৃত্ব দানের কারণে ১৯৮৩ সালের ডিসেম্বর মাসের প্রথম সপ্তাহে সামরিক বাহিনী তাঁকে গ্রেফতার করে। সামরিক ক্যান্টেনমেন্টে নিয়ে গিয়ে ৯ দিন ৯ রাত তাঁর উপর অমানুষিক নির্যাতন চালানো হয়। কারগার থেকে মুক্ত হওয়ার পরও তিনি সংগ্রামের ধারা অব্যাহত রাখেন। \r\nএছাড়াও সারাদেশে  ছাত্রদের সংগঠিত করে ছাত্র আন্দোলনের সূত্রপাত এবং ছাত্র সংগ্রাম পরিষদ গঠন করে মজিদ কমিশন শিক্ষানীতির বিরুদ্ধে শিক্ষাভবন ঘেরাও করে বাংলাদেশে সামরিক ও স্বৈরাচার বিরোধী আন্দোলনের সূত্রপাত ঘটান। সেক্ষেত্রে ১৯৮৮ সালের ৩১ মে থেকে শুরু হয় রাজশাহীতে জামাত শিবিরের সন্ত্রাসী কার্যক্রম। ৩১ তারিখে এই সন্ত্রাসীরা রাজশাহী মেডিক্যাল কলেজের অভ্যন্তরে শহীদ ডা. জামিল আকতার রতনকে মর্মান্তিকভাবে হত্যা করে। এর প্রতিবাদে তখন থেকে তিনি স্বাধীনতা বিরোধী জামাত শিবিরের বিরুদ্ধে মুক্তিযুদ্ধের পক্ষের শক্তিকে ঐক্যবদ্ধ করে রাজশাহীসহ জাতীয় পর্যায়ে মংগ্রাম গড়ে তোলার ক্ষেত্রে সর্বাত্মক দায়িত্ব পালন করেন। এছাড়ও জাতীয় রাজনীতিতে স্বৈরাচার ও সামরিক সরকার বিরোধী অন্যতম সংগঠক হিসেবে তিনি গুরুপ্তপূর্ণ ভূমিকা পালন করেন। মোট কথা ১৯৯০ সালে সামরিক ও স্বৈরাচার পতন আন্দোলনে তিনি রাজশাহীসহ ঢাকায় আপসহীন ভূমিকা পালন করেন। এজন্য তাঁকে বহুবার জেলে যেতে হয়েছে।   \r\n\r\n১৯৯১ সালে তিনি বাংলাদেশের ওয়ার্কার্স পার্টির পলিটব্যুরোর সদস্য পদ লাভ করেন। তারপর থেকে তিনি দেশের বিভিন্ন অঞ্চলে কৃষি, কৃষক ও শ্রমিকদের ন্যায্য দাবী আদায়ের আন্দোলনের ধারা অব্যাহত রাখতে সক্রিয় ভূমিকা পালন করেন। এসময় তিনি বরেন্দ্র অঞ্চলে সেচ আন্দোলন এবং শিল্প-কলকারখানা বেসরকারিকরণের বিরুদ্ধে আন্দোলনে নেতৃত্ব দান করেন। ১৯৯৩ সালে শহীদ জুবায়ের চৌধুরী রিমু এবং শহীদ রুপম হত্যার বিচারের দাবিতে তিনি সৌচ্চার হন। এ আন্দোলনে নেতৃত্ব দানের মধ্য দিয়ে জামাতকে নিষিদ্ধ করার দাবী রাজশাহীর সমাবেশ থেকে প্রথম উচ্চারিত হয়। তাঁর রাজনৈতিক প্রজ্ঞা ও দূরদর্শিতা প্রশংসার দাবী রাখে। জননেতা ফজলে হোসেন বাদশা বিষয়টি মর্মে মর্মে উপলব্ধি করেছেন এবং সে মতো দেশের ছাত্র সমাজ, যুবশক্তি ও মুক্তিযুদ্ধের চেতনায় বিশ্বাসী জনগণকে সংগঠিত করার কাজে দিন রাত নিরলস পরিশ্রম করেছেন। ১৯৯৩ সালে আদিবাসীদের নায্য অধিকার প্রতিষ্ঠার প্রশ্নে জাতীয় আদিবাসী পরিষদ গঠনে তিনি মুখ্য ভূমিকা পালন করেন। এ পরিষদে তাঁকে প্রধান উপদেষ্টা হিসেবে সম্মান জনানো হয়। তিনি আদিবাসীদের সাংবিধানিকভাবে স্বীকৃতি আদায়ের জন্য সারাদেশে আদিবাসীদের সংগঠিত করেন এবং জাতীয় পর্যায়ে তাদের দাবি দাওয়া আদায়ের জন্য সরকারের কাছে বিভিন্ন সময় চাপ সৃষ্টি করেন। তিনি চির অবহেলিত ও নিপীড়িত-নির্যাতিত আদিবাসীদের আপনজন হিসেবে অত্যন্ত বলিষ্ঠ ভূমিকা পালন করেন। আদিবাসী ছেলে মেয়েরা যেন নিজেদের ভাষা ও সংস্কৃতির ছত্রছায়ায় লেখাপড়া করতে পারে, সে জন্য তিনি বরেন্দ্র অঞ্চলে সর্ব প্রথম দুটি আদিবাসী স্কুল প্রতিষ্ঠিত করেন। আদিবাসীরা যেন আইনী সহায়তা পায় সেজন্য তিনি বাস্তবভিত্তিক কর্মসূচি প্রণয়ন করেন। এছাড়াও তাদের  চাকরীর বিষয়ে কৌটা বৃদ্ধির জন্য লড়াকু  সেনাপতির ভূমিকায় অবতীর্ণ হন। আদিবাসীদের জীবনে শান্তি প্রতিষ্ঠার ক্ষেত্রে আদিবাসী নেতা সন্তু লারমাসহ সর্বস্তরের রাজনীতিবিদ ও কর্মীদের সঙ্গে তিনি নিয়মিত আলাপ আলোচনা চালিয়েছেন। এর ফলে আদিবাসীদের ন্যায্য দাবী গণমানুষের দাবীতে পরিণত হয়েছে।  \r\n\r\nতিনি ১৯৯৬ সালে বামজোট এবং ১১ দলকে নিয়ে সকল প্রগতিশীল আন্দোলনে নেতৃত্ব দিয়েছেন। এসময় তিনি ১৯৭২ সালের সংবিধান পুন: প্রতিষ্ঠার আন্দোলনে সম্পৃক্ত থেকেছেন এবং জাতিকে কলঙ্কমুক্ত করার লক্ষে স্বাধীনতা বিরোধীদের বিচারের কাঠগড়ায় দাঁড় করানোর জন্য শহীদ জননী জাহানারা ইমামকে নানাভাবে সহায়তা করেছেন। ১৯৯৩ সালে গণতান্ত্রিক ও প্রগতিশীল চিন্তা চেতনার একজন বলিষ্ঠ পুরোধা ব্যক্তিত্ব হিসেবে তিনি নাচোল বিদ্রোহের অগ্নিকন্যা ইলা মিত্রকে রাজশাহী ও চাঁপাইনবাবগঞ্জের নাচোলে নিয়ে আসেন এবং তাঁকে গণসংবর্ধনা দেন। জীবনের শেষ প্রান্তে দাঁড়িয়ে রানীমাতা ইলা মিত্র এই সম্মান লাভ করে আনন্দ ও গৌরব বোধ করেন। এছাড়াও বাংলা সাহিত্যের স্বনামখ্যাত লেখক মহাশ্বেতা দেবীকে তিনি রাজশাহী ও নওগাঁয় আদিবাসীদের মধ্যে নিয়ে আসেন।\r\n\r\n২০০১ সালে জামাত বিএনপি আবার ক্ষমতায় এলে সাম্প্রদায়িক ও প্রতিক্রয়াশীল শক্তির তৎপরতা বৃদ্ধি পায়। এসময় ফজলে হোসেন বাদশা মুক্তিযুদ্ধের চেতনায় বিশ্বাসী মানুষদের নিয়ে তাদের অশুভ তৎপরতার বিরুদ্ধে বলিষ্ঠ ও গতিশীল নেতৃত্ব দান করেন। মিটিং, মিছিল, সভা ও সেমিনার আয়োজনের মধ্যে দিয়ে তাদের দাঁতভাঙ্গা জবাব দেয়া হয়।\r\n\r\nএরি মধ্যে ২০০২ সালে রাজশাহী সিটি কর্পোরেশনের নির্বাচনে মেয়র প্রার্থী হিসেবে ফজলে হোসেন বাদশা নাগরিক কমিটির পক্ষ হতে নির্বাচন করেন। রাজশাহী মহানগরীর জনগণ তাঁকে নির্বাচিত করলেও কারচুপির মাধ্যমে নির্বাচনের রায় পরিবর্তন  করা হয়। তৎকালীন সরকারের এ ধরনের কার্যক্রম রাজশাহীসহ সারাদেশে নিন্দার ঝড় তোলে। আর এজন্যই একজন সৎ ও নিবেদিতপ্রাণ রাজনীতিক হিসেবে জননেতা ফজলে হোসেন বাদশার সর্বস্তরের মানুষের কাছে ঈর্ষণীয় গ্রহণযোগ্যতা বহুলাংশে বৃদ্ধি পায়। ২০০৪ সালে ৪ দলীয় ঐক্যজোট বিএনপি সরকারের ছত্রছায়ায় রাজশাহীর বাগমারায় জামাত ও শিবিরচক্র বাংলাদেশকে পাকিস্তান বা আফগানিস্তান বানানোর লক্ষে তাদের জঙ্গিবাদী তৎপরতা চালিয়ে যেতে থাকে। প্রশাসনের নাকের ডগায় বাংলা ভাই সৃষ্টি হয়। রাজশাহী মহানগরীতেও বাংলাভায়ের নেতৃত্বে জঙ্গীদের সশন্ত্র মিছিল হয়। একাজে তৎকালীন সরকার তাদের সবরকম সহযোগিতা করে। চার দলীয় জোটের এই দু:শাসনের বিরুদ্ধে মুক্তিযুদ্ধের চেতনায় উজ্জীবিত হয়ে মুক্তিযুদ্ধের সকল শক্তিকে ঐক্যবদ্ধ করে জননেতা ফজলে হোসেন বাদশা তাদের বিরুদ্দে প্রতিবাদ ও প্রতিরোধ আন্দোলন গড়ে তোলেন।', 'five.jpg'),
(6, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'চার. রাজনৈতিক জীবন ', ' তিনি জীবনের ঝুঁকি নিয়ে বাগমারাতেও হাজার হাজার গণমানুষের সহায়তায় মহাসমাবেশ আয়োজন করেন। এর ফলে জাতীয় ও আন্তর্জাতিক পর্যায়ে বিএনপি-জামাতের জঙ্গিবাদী তৎপরতা ব্যাপকভাবে প্রচারিত ও নিন্দিত হয়। এ অবস্থার প্রেক্ষাপটে ২১ শে আগস্টের গ্রেনেড হামলা  এবং সারাদেশে একসঙ্গে বোমা বিস্ফোরণের পর মুক্তিযুদ্ধের পক্ষের সকল শক্তিকে একত্রিত করে ১৪ দল গঠিত হয়। এ উদ্যোগের একজন সফল ও বলিষ্ঠ নেতা হিসেবে তিনি প্রশংসিত হন। দেশে গণতান্ত্রিক পরিবেশ ফিরিয়ে আনার জন্য তাঁর আপসহীন গতিশীল ভূমিকা জনমনে এবং রাজনীতিতে নতুন মাত্রা যুক্ত হয়। বাংলা ভাইয়ের বিরুদ্ধে তিনি জীবন বাজি রেখে যে ভূমিকা পালন করেন আমাদের রাজনীতির ইতিহাসে স্মরণীয় অধ্যায় হিসেবে বিবেচিত হবে। তাঁর রাজনৈতিক জীবন যে গণমানুষের মুক্তির সোপন রচনা করে তা প্রমাণিত হলো পরবর্তী পর্যায়ে জাতীয় সংসদ নির্বাচনে বিপুল ভোটের ব্যবধানে তাঁর বিজয় অর্জনের মধ্য দিয়ে।', 'six.jpg'),
(7, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'পাঁচ. জাতীয় সংসদ সদস্য হিসেবে জয়লাভ', 'তিনি নবম জাতীয় সংসদ নির্বাচনে মহাজোটের প্রার্থী হিসেবে রাজশাহী-২ সদর আসনে সংসদ সদস্য নির্বাচিত হন। বর্তমানে তিনি অনুমিত হিসাব সংক্রান্ত সংসদীয় কমিটির সদস্য, আদিবাসী বিষয়ক সংসদীয় ককাস কমিটির সদস্য এবং গণতান্ত্রিক বাজেট আন্দোলনের সংসদীয় ককাসের সহ-সভাপতি হিসেবে দায়িত্ব পালন করেছেন। তিনি রাজশাহীসহ সারাদেশের জনগণের ন্যায্য দাবী-দাওয়া, অধিকার আদায় ও দেশের স্বার্থ রক্ষার প্রশ্নে যেমন সংসদের ভেতর তেমনি সংসদের বাইরে রাজপথে আপসহীন  ভূমিকা পালন করেছেন। দীর্ঘদিন পর রাজশাহী থেকে নির্বাচিত একজন সংসদ সদস্যকে রাজশাহীর জনগণের পক্ষে জাতীয় সংসদে ভূমিকা রাখতে দেখা যায় তিনি ফজলে হোসেন বাদশা । জাতীয় সংসদে তাঁর প্রদত্ত ভাষণসমূহ রাজশাহীর জনগণের মধ্যে আলোড়ন সৃষ্টি হয়। সংবিধানের পঞ্চদশ সংশোধনীর উপর জাতীয় সংসদে গুরুত্বপূর্ণ ভিন্নমত প্রকাশ করে তিনি দৃষ্টান্ত স্থাপন করেন। জাতীয় সংসদ সদস্য হিসেবে শপথ গ্রহণের পর দিন থেকেই তিনি রাজশাহীসহ জাতীয় পর্যায়ের বিভিন্ন গুরুত্বপূর্র্ণ সমস্যা চিহ্নিতকরণ এবং সে সবের সমাধানের জন্য আপ্রাণ চেষ্টা চালিয়ে যাচ্ছেন। বিশেষ করে তাঁর নির্বাচনী এলাকায় রাজশাহী মহানগরীর অর্থাৎ রাজশাহী সিটি কর্পোরেশনর আওতাভুক্ত। এই এলাকার শিক্ষা প্রতিষ্ঠান, স্কুল-কলেজ ও মাদ্রাসার শিক্ষার মান উন্নয়নসহ অবকাঠামোগত উন্ন্য়ন চোখে পড়ার মতো। তিনি ব্যক্তিগত জীবনে যেমন সৎ, নিষ্ঠাবান, দায়িত্ব ও কর্তব্যপরায়ন তেমনি জাতীয় সংসদ সদস্য হিসেবেও সততার এক অনন্য দৃষ্টান্ত স্থাপন করে চলেছেন। তিনি সবচেয়ে বড় একটি মহৎ কর্ম সম্পাদন করেছেন সেটি হল হাজার বছরের সর্বশ্রেষ্ঠ বাঙ্গালি জাতির জনক বঙ্গবন্ধু শেখ মুজিবুর রহমানের অন্যতম বিশ্বস্ত’, সহচর, মহান মুক্তিযুদ্ধের অন্যতম সংগঠক, রাজশাহীর সর্বস্তরের জনগণের নয়নমণি জাতীয় নেতা শহীদ এএইচএম কামারুজ্জমানের নামে উপশহরে হাউজিংএস্টেট কলেজের নামকরণের উদ্যোগ গ্রহণ। এ কলেজের গভর্নিং বডির সভাপতি হিসেবে তিনি টিনশেড ঘর ভেঙ্গে  চার তলা বিশিষ্ট পাকা ভবনের ব্যবস্থা করেন। তিনি রাজশাহী মহানগরীতে প্রতিষ্ঠিত সবচেয়ে পুরাতন বে-সরকারি কলেজ শাহ্ মখদুম কলেজে এই প্রথম ৪৪ বছরের ইতিহাসে একটি দৃষ্টিনন্দন শহীদ-মিনার প্রতিষ্ঠা করেন। তিনি ১৯৫২ সালে ভাষা আন্দোলনের সময় জন্মগ্রহণ করেন এবং সেই চেতনা দ্বারা পথ চলেন বলেই শাহ্ মখদুম কলেজসহ আরো কয়েকটি  শিক্ষা প্রতিষ্ঠানে শহীদ মিনার নির্মাণের ব্যবস্থা করেন। বরেন্দ্র কলেজে দীর্ঘ সময় ধওে দুই শিফটের ছাত্র শিক্ষকদের মধ্যে নানা রকম জটিল ও মারমুখি সমস্যা  বিরাজ করছিল, সেই  সমস্যার সুষ্ঠু ও সুন্দর সমাধান করে দিয়েছেন তিনি। এখন এ শিক্ষা প্রতিষ্ঠানে  শান্তি বিরাজ করছে এবং চমৎকার শিক্ষার পরিবেশে শিক্ষা কার্যক্রম পরিচালিত হচ্ছে। শিক্ষা মন্ত্রাণালয় থেকে পদ্মা আবাসিক এলাকায় অবস্থিত বঙ্গবন্ধু কলেজের জমির মূল্য নামমাত্র টাকায় পরিশোধের ব্যবস্থা করে দিয়ে তিনি এই প্রতিষ্ঠানকে  ঋণমুক্ত করেছেন। তিনি দেশের ঐতিহ্যবাহী শিক্ষা প্রতিষ্ঠান রাজশাহী কলেজে পুনরায় ইন্টারমিডিয়েট শাখা খোলার বিষয়ে প্রয়োজনীয় পদক্ষেপ গ্রহণ করেছেন। এছাড়াও রাজশাহী নিউ গভ. ডিগ্রি কলেজ এবং রাজশাহী সরকারি মহিলা কলেজে কয়েকটি বিষয়ে অনার্র্সসহ এম.এ.শ্রেণি খোলার ব্যবস্থা করেন। তিনি মহানগরীর প্রতিটি জামে মসজিদ ও মন্দিরে আর্থিক সহয়তা  দান করেছেন। আবার কয়েকটি জীর্ণ পুরাতন মসজিদ ভেঙ্গে নতুন ভবনের জন্য অর্থ বরাদ্দ করেছেন। একথা সর্বজনবিদিত যে, জনগণের প্রয়োজনে যে নেতা  রাজনীতি করেন সেই নেতার প্রতি জনগণের হৃদয়ের গভীর থেকেই শ্রদ্ধা ও ভালোবাসার সৃষ্টি হয়। সেই নেতাকে মানুষ তাদের এক বুক কাছের মানুষ হিসেবেই মূল্যায়ন করেন। এমনি ধরনেরই একজন মহান নেতা ফজলে হোসেন বাদশা। জীবনে কখনোই তিনি জনগণের সাথে অশোভন আচরণ করেননি কিংবা তাঁর আচার-ব্যবহারে কখনোই সামান্যতম অহংকার পরিলক্ষিত হয়নি। সর্বদাই সবার সঙ্গে হাসিমুখে কথা বলা এবং দু:স্থ ও গরীব মেহনতী মানুষের পরম বন্ধু হিসেবে তাঁর পরিচিতি প্রশংসার দাবী রাখে। তিনি শুধু রাজশাহীর নয় সারা বাংলাদেশে পরীক্ষিত ও আপসহীন জননন্দিত কেন্দ্রীয় বা জাতীয় পর্যায়ের একজন নেতা। এ কথা অনস্বীকার্য যে, আমাদের দেশে অধিকাংশ রাজনীতিকের বাড়ি, গাড়ি, বিষয়-সম্পদ এবং আয়-উর্পাজনের ক্ষেত্রে জনগণের মুখেমুখে নানা রকম নিন্দনীয় কটুক্তি শোনা যায়। কিন্তু ব্যতিক্রম চরিত্রের  অধিকারী ফজলে হোসেন বাদশা। তাঁর সহজ-সরল অনাড়ম্বর জীবন যাপন এবং উন্নত চিন্তা ও জীবন দর্শন তাঁকে আজকের অবস্থানে স্থায়ী ঠাঁই করে দিয়েছে। \r\n\r\nবাংলাদেশ কৃষি প্রধান দেশ। এ দেশের শতকরা ৮০ ভাগ মানুষ গ্রামে বাস করেন। তাদের প্রধান আয় উর্পাজন কৃষি থেকে। আগামী জীবনে কৃষক সমাজের কাছে তাঁর এই মহান প্রস্তাব জাতীয় সংসদে পাস হলে আইনে পরিনত হবে এবং তা যথারীতি বাস্তবায়ন হলে শত শত বছরের ন্যায্য অধিকার বঞ্চিত মানুষগুলোর জীবনে নতুন দিগন্ত উন্মচিত হবে। দেশ অচিরেই একটি মধ্যম আয়ের দেশ হিসাবে পরিনত হবে এবং জীবন যাত্রার মানও বৃদ্ধি পাবে। কৃষি আদালতের স্বাপ্নিক ফজলে হোসেন বাদশার নামও সেসঙ্গে  প্রতিনিয়তই কীতিত্ব হবে। বাদযাবে\r\n', 'seven.jpg'),
(8, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'ছয়. বিদেশ ভ্রমণ', 'ফজলে হোসেন বাদশা জাতীয় সংসদ সদস্য নির্বাচিত হওয়ার পূর্বে বহুবার বহুদেশে ব্যক্তিগত এবং দলগত বিভিন্ন কাজে বিদেশ ভ্রমণ করেছেন। আন্তর্জাতিক মানের বিভিন্ন সেমিনার ও সম্মেলনে অংশগ্রহণ অথবা মূল বক্তা হিসেবে তিনি বিদেশে গেছেন। রাজনৈতিক দলের প্রতিনিধি হিসেবে এবং সংসদ সদস্য হিসেবে বিভিন্ন দেশে বাংলাদেশকে প্রতিনিধিত্ব করেছেন। তিনি ভারত, নেপাল, শ্রীলঙ্কা, থাইল্যান্ড, ইংল্যান্ড, জার্মান, রাশিয়া, চীন, জাপান, কোরিয়া, আমেরিকা, হংকং প্রভৃতি দেশ ভ্রমণ করেছেন। ২০১১ সালের ডিসেম্বর মাসে তিনি বাংলাদেশ সরকারের প্রতিনিধি দলের সঙ্গে জাতিসংঘের সাধারণ পরিষদের এক অধিবেশনে যোগদান করেন এবং সেখানে বাংলাদেশকে প্রতিনিধিত্ব করে বক্তব্য উপস্থাপন তাঁর জীবনের একটি স্মরণীয় বিষয় হিসেবে চিহ্নিত হয়ে রয়েছে। এজন্য তিনি প্রতিনিয়তই গৌরব অনুভব করেন।', 'eight.jpg'),
(9, 'ফজলে হোসেন বাদশা : জীবন ও সংগ্রাম', 'ড. তসিকুল ইসলাম রাজা', 'সাত. উপসংহার', 'ফজলে হোসেন বাদশা আপসহীন ও সংগ্রামী মানুষের নাম। ছাত্র রাজনীতিতে দৃষ্টান্ত সম্পন্ন একজন মানুষ হিসেবে ছাত্র-রাজনীতির ইতিহাসে তিনি স্মরণীয় হয়ে থাকবেন। ছাত্র রাজনীতি আজ অনেকটা আদর্শচ্যুত। পেশী শক্তি, অস্ত্রের ঝনঝনানি, ক্ষমতার দাপট, কালো টাকার গরম, টেন্ডারবাজী, নিত্যদিনের ঘটনা হিসেবে এবং ছাত্রদের দলের লেজুর বৃত্তির বিষয়টি এখন পত্রিকায় প্রথম পাতায় গুরুত্বসহকারে ছাপা হয়। সেখানে ফজলে হোসেন বাদশা ছাত্র নেতা হিসেবে এবং বাংলাদেশ ওয়ার্কার্স পার্টির পলিটব্যুরোর একজন সদস্য হিসেবে বাংলাদেশের রাজনীতিতে আদর্শমণ্ডিত গণমানুষের নেতা হিসেবে যে  দৃষ্টান্ত স্থাপন করেছেন সেজন্য বাংলাদেশের রাজনীতির ইতিহাসে তাঁর নাম স্বর্ণাক্ষরে লেখা থাকবে। জাতীয় সংসদ সদস্য হিসেবে তিনি অত্যন্ত সততা, দক্ষতা, ও বিচক্ষণতার সঙ্গে দায়িত্ব পালন করেছেন। আমাদের দেশে অনেক সংসদ সদস্য অসৎ উদ্দেশ্য চরিতার্থ করার অভিপ্রায়ে প্রতিদিন মন্ত্রণালয়ে তদবিরকারক হিসেবে চিহ্নিত হয়েছেন। কিন্তু ব্যতিক্রম ফজলে হোসেন বাদশা। তাঁর নামে এ ধরনের কোন খারাপ মন্তব্য কখনো শোনা যায়নি। তিনি প্রায় ৪৫ বছরের রাজনৈতিক জীবনে সর্বদাই একটি শোষনহীন, অর্থনৈতিকভাবে সমৃদ্ধ এবং রাজাকার মুক্ত একটি বাংলাদেশে গড়ার জন্য দৃঢ়প্রতিজ্ঞ। তিনি ভাষা আন্দোলন ও মুক্তিযুদ্ধের চেতনাকে ধারন ও লালন করে মুক্তিযুদ্ধের বিরোধী শক্তির মূল শিকড় উৎপাটনের মধ্য দিয়ে ঘাতকদের বিচার ও ফাঁসির রায় বাস্তবায়ন দেখতে চান। জাতির কলঙ্ক মুক্ত করার জন্যই তাঁর এই প্রত্যাশার সঙ্গে সংগ্রামী জনগণ আজ একাত্মতা ঘোষণা করেছেন। জনগণের সেবা করার মন-মানসিকতা থেকেই তিনি পরিচ্ছন্ন ও আপসহীন রাজনীতি করেন। তাঁর রাজনৈতিক জীবনে কোন ফাঁক ও ফাঁকির সুযোগ নেই। তাই আমরা দৃঢ়চিত্তে  পরিশেষে বলবো, ফজলে হোসেন বাদশার সংগ্রামী জীবন চিরকাল আলোকস্তমে¢র মতোই রাজশাহী মহানগরবাসীসহ জাতিকে সুন্দর পথ দেখাবে।  ', 'nine.jpg');

